import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News  extends Component {
    constructor(){
        super();
        this.state ={
            articles : [],
            loading:false

        }
    }


    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=ba3897c4f0d94cf2a56ac063a2fdcaa3&page=1";
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles : parsedData.articles})
    }

    handlepreviousclick = ()=>{
        console.log("previous")
        
    }

    handlenNextClickn =()=>
    {   
        this.setState({
                page:this.state.page+1,
                articles :parsedData.articles
        })
       
    }

  render() {
    return (
      <div>
        <div className="container my-3 ">
                    <h1>Daily News Top--headlines!!</h1>
                   <div className="row">
                    {this.state.articles.map((element)=>{
                        return <div className="col-md-4" key = {element.url}>
                            <NewsItem  title={element.title?element.title.slice(0,40):""}  description = {element.description?element.description.slice(0,80):""}  imageUrl= {element.urlToImage}
                            newsUrl = {element.url}/>
                        </div>  
                    })}        
                   </div>

                   <div className="container my-1 d-flex justify-content-between">
                        <button disabled = {this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handlepreviousclick}> &larr; Previous</button>
                        <button type="button" class="btn btn-dark" onClick={this.handlenNextClick}>Next &rarr; </button>
                   </div>
        </div>
      </div>
    )
  }
}

export default News
